import React from 'react';
import './App.css';
import Unsplash , {toJson} from 'unsplash-js';

const searchKeyword = "simplicity"

const unsplash = new Unsplash({
  applicationId: "3039697e14f11da7d872ff9adbe6cf251f1796791d531c97c82874c8beb4ff7f",
  secret: "2f4ccf420a9a9291efc571fff88d05dc6cb4b0b90cec8a4a03a121d97062a6a3"
});


function App() {
  let url = "";

  //const url = "https://source.unsplash.com/collection/1971015/400x400/?sig=";

  const isLoading = () => {
    if (url==="")
      return true;
    return false;
  }

  const getPhoto = async () => {
    try {
      const response = await unsplash.photos.getRandomPhoto({ width:400, height:400, query: searchKeyword});
      const json = await toJson(response);
      url = json.urls.custom;
      console.log(json);
      return json;
    } catch (e) {
      console.error(e);
    }
    // .then(toJson)
    // .then(json => {
    //   url= json.links.html;
    //   console.log(json);
    // });
  };


  return (
    <div className="App">
      <header className="Header-Text">
        Good Morning!
      </header>
      <div className="Body-Picture">
        {isLoading ? getPhoto() : <img src={url} alt="unsplash"/>}
        {console.log("I'm here with url " + url)}
      </div>
      <div className="Footer-Credits">
        Made with {"<3"} by  <a id="websiteLink" href="https://mesagoh.github.io"> Melissa Goh </a> , 2019
      </div>
    </div>
  );
}

export default App;
